mapper.select:
select sabbatical_confirmation_info.id, sabbatical_confirmation_info.is_agree, sabbatical_confirmation_info.user_serial, personal_info.user_name, sabbatical_confirmation_info.approval_datetime from sabbatical_confirmation_info, sabbatical_application_info, personal_info where personal_info.user_serial = sabbatical_application_info.user_serial and sabbatical_application_info.id = sabbatical_confirmation_info.id
and personal_info.user_name = #{userName}
and sabbatical_confirmation_info.is_agree = #{isAgree}
order by sabbatical_confirmation_info.approval_datetime desc;
